import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/authService/auth-service.service';
import { Plans } from 'src/app/services/interface/movie-interface';

@Component({
  selector: 'app-register-three',
  templateUrl: './register-three.component.html',
  styleUrls: ['./register-three.component.css'],
})
// export class RegisterThreeComponent {
//   constructor(private router: Router, private authService: AuthService) {}

//   // Todo: Add selector to this page
//   onNext() {
//     this.authService.setNewUser({ role: 'USER' });
//     this.authService.registerUser().subscribe();
//     // this.authService.clearNewUser();
//     this.router.navigate(['/home']);
//   }
export class RegisterThreeComponent {
  myRole: string | null = null;
  constructor(private router: Router, private authService: AuthService) {}

  onSelect(plan: string): void {
    if (plan === 'Basic with ads') {
      this.myRole = 'USER';
    } else if (plan === 'Standard') {
      this.myRole = 'ADMIN';
    } else if (plan === 'Premium') {
      this.myRole = 'SUPER';
    }
  }

  onNext(): void {
    if (this.myRole) {
      const localRole = localStorage.getItem('role');
      if (!localRole) {
        console.log('user not registered');
        // User not registered
        this.authService.registerUser({ role: this.myRole }).subscribe();
        localStorage.setItem('role', this.myRole);
      } else {
        // User already registered
        console.log('user already registered');
        if (localRole !== this.myRole) {
          localStorage.setItem('role', this.myRole);
          this.authService.updateRole({ role: this.myRole }).subscribe();
        }
      }
    }
  }
}
<div class="container">
  <app-nav-bar></app-nav-bar>
  <div class="pageContainer">
    <div class="topContainer">
      <p>STEP 3 OF 3</p>
      <h1>Choose the plan that's right for you</h1>
      <ul>
        <li>Watch all you want.</li>
        <li>Recommendations just for you.</li>
        <li>Change or cancel your plan anytime.</li>
      </ul>
    </div>

    <div class="bottomContainer">
      <div class="row first-row">
        <div class="contentContainer left">
          <button class="first-column box" (click)="onSelect('Basic with ads')">
            <p>Basic with ads</p>
          </button>
          <button class="second-column box" (click)="onSelect('Standard')">
            <p>Standard</p>
          </button>
          <button class="third-column box" (click)="onSelect('Premium')">
            <p>Premium</p>
          </button>
        </div>
      </div>


      <div class="row">
        <p class="feature">Monthly Price</p>
        <div class="contentContainer">
          <p class="first-column">$6.99</p>
          <p class="second-column">$15.49</p>
          <p class="third-column">$15.99</p>
        </div>
      </div>

      <div class="row">
        <p class="feature">Video quality</p>
        <div class="contentContainer">
          <p class="first-column">Good</p>
          <p class="second-column">Better</p>
          <p class="third-column">Best</p>
        </div>
      </div>

      <div class="row">
        <p class="feature">Resolution</p>
        <div class="contentContainer">
          <p class="first-column">720p</p>
          <p class="second-column">1080p</p>
          <p class="third-column">4K+HDR</p>
        </div>
      </div>

      <div class="row">
        <p class="feature">Watch on your TV, computer, mobile phone and tablet</p>
        <div class="contentContainer">
          <p class="first-column">YES</p>
          <p class="second-column">YES</p>
          <p class="third-column">YES</p>
        </div>
      </div>

      <div class="row last-row">
        <p class="feature">Download</p>
        <div class="contentContainer">
          <p class="first-column">NO</p>
          <p class="second-column">YES</p>
          <p class="third-column">YES</p>
        </div>
      </div>

    </div>
    <div>
      <button type="next" class="nextButton" (click)='onNext()'>Next</button>
    </div>
  </div>
  <!-- <app-footer></app-footer> -->
</div>
