import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.scss'],
})
export class AppComponent {
  loginForm!: FormGroup;
  constructor(private formBuilder: FormBuilder) {
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
    });
  }
  get email() {
    return this.loginForm.get('email');
  }

  get password() {
    return this.loginForm.get('password');
  }

  onSubmit() {
    console.log(this.loginForm.value);
  }
}
<main>
  <app-nav-bar></app-nav-bar>

  <div class="container">
    <form>
      <h1 style="text-align: center;">Sign In</h1>

      <form [formGroup]="loginForm" (ngSubmit)="onSubmit()" novalidate>
        <p>
          <label for="email">Email </label>
          <input type="text" id="email" name="email" formControlName="email" placeholder="Email or phone number *">
        </p>
        <div
          *ngIf="loginForm.get('email')?.invalid && (loginForm.get('email')?.dirty || loginForm.get('email')?.touched)">
          <div [hidden]="!loginForm.get('email')?.errors?.['required']">
            Email is required
          </div>
          &nbsp;
          <div [hidden]="!loginForm.get('email')?.errors?.['email']">
            Invalid email format
          </div>
        </div>

        <p>
          <label for="password">Password </label>
          <input type="text" id="password" name="password" formControlName="password"
            placeholder="password or phone number *">
        </p>
        <div
          *ngIf="loginForm.get('password')?.invalid && (loginForm.get('password')?.dirty || loginForm.get('password')?.touched)">
          <div [hidden]="!loginForm.get('password')?.errors?.['required']">
            password is required
          </div>
        </div>
        <p>
          <button type="submit" [disabled]="!loginForm.valid">Submit</button>
        </p>

      </form>
      <div class="remember-help-container">
        <span><input type="checkbox" name="remember-me" value="remember me" checked id="checkbox">Remember Me</span>
        <a href="#" id="help">Need help?</a>
      </div>
      <div class="facebook-login">
        <img src="../../assets/facebook.svg">
        <a href="#">Login with Facebook</a>
      </div>

      <p class="signup">New to Notflix? <a href="#" id="signup">Sign up now</a></p>
      <p>This page is protected by Google reCAPTCHA to ensure you're not a bot. <a id="learnmore">Learn more.</a></p>
    </form>
  </div>
</main>
