import { Component } from '@angular/core';
import {
  AbstractControl,
  FormBuilder,
  FormGroup,
  Validators,
} from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent {
  registerForm1!: FormGroup;
  constructor(private formBuilder: FormBuilder, private http: HttpClient) {
    this.registerForm1 = this.formBuilder.group({
      email: [
        '',
        {
          validators: [Validators.required, Validators.email],
          asyncValidators: [this.emailExistsValidator.bind(this)],
          updateOn: 'blur',
        },
      ],
      password: ['', Validators.required],
    });
  }
  get email() {
    return this.registerForm1.get('email');
  }

  get password() {
    return this.registerForm1.get('password');
  }

  onSubmit() {
    console.log(this.registerForm1.value);
  }

  checkEmailExists(email: string): Observable<boolean> {
    return this.http.get<any>(`/auth/check-email?email=${email}`).pipe(
      map((response) => {
        return response.exists;
      })
    );
  }
  emailExistsValidator(
    control: AbstractControl
  ): Observable<{ [key: string]: any } | null> {
    return this.checkEmailExists(control.value).pipe(
      map((exists) => {
        return exists ? { emailExists: true } : null;
      })
    );
  }
}
<div class="container">
  <!-- <app-header></app-header> -->
  <form class="form">
    <p>Step 1 of 3</p>
    <h1>Create a password to start your membership</h1>
    <p>Just a few more steps and you're done!</p>
    <p>We hate paperwork, too.</p>
    <br>
    <div class="userInputContainer">
      <form [formGroup]="registerForm1" (ngSubmit)="onSubmit()" novalidate>
        <p>
          <!-- <label for="email">Email </label> -->
          <input type="text" id="email" name="email" formControlName="email" placeholder="Email or phone number *">
        </p>
        <div
          *ngIf="registerForm1.get('email')?.invalid && (registerForm1.get('email')?.dirty || registerForm1.get('email')?.touched)">
          <div [hidden]="!registerForm1.get('email')?.errors?.['required']">
            Email is required
          </div>
          &nbsp;
          <div [hidden]="!registerForm1.get('email')?.errors?.['email']">
            Invalid email format
          </div>
          <div *ngIf="email?.errors?.['emailExists']">Email already exists!</div>
        </div>

        <p>
          <!-- <label for="password">Password </label> -->
          <input type="text" id="password" name="password" formControlName="password"
            placeholder="password or phone number *">
        </p>
        <div
          *ngIf="registerForm1.get('password')?.invalid && (registerForm1.get('password')?.dirty || registerForm1.get('password')?.touched)">
          <div [hidden]="!registerForm1.get('password')?.errors?.['required']">
            password is required
          </div>
        </div>
        <p>
          <button class="nextButton" type="submit" [disabled]="!registerForm1.valid">Next</button>
        </p>
      </form>
    </div>

  </form>
  <!-- <app-footer></app-footer> -->
</div>
