import { Component, Inject } from '@angular/core';

interface signupData {
  username: string;
  password: string;
  email: string;
  role: string;
  tmbd_key: string;
}


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})

export class RegisterComponent {
    data: signupData = {
      username: "",
      password: "",
      email: "",
      role: "",
      tmbd_key: ""
    }
    screen: number = 1;

    constructor() { 
      this.next_screen = this.next_screen.bind(this);
      this.prev_screen = this.prev_screen.bind(this);
    }

    next_screen() {
      if (this.screen < 3) {
        this.screen += 1;
    }
  }

    prev_screen() {
      if (this.screen > 1) {
        this.screen -= 1;
      }
    }

    update_page1(inputData : Object) {

    }
    update_data(inputData : Object) {
      this.data = {...this.data, ...inputData};
    }

    submit_form() {
      console.log(this.data);
    }

}
